import React, {useState, useEffect} from 'react'
import BookRow from './BookRow'
import BookSearch from './BookSearch'

export default function BooksPage() {

    const [books, setBooks] = useState([]);
    const [course, setClass] = useState("");
    const [college, setCollege] = useState("");

    const getBooks = async () => {
        try {
            const response = await fetch('http://localhost:5000/getbooks')
            const data = await response.json();
            setBooks(data);
        } catch (error) {
            console.error(error.message);
        }
    }

    const getSomeBooks = async (college, course) => {
        try {
            const response = await fetch(`http://localhost:5000/getbooks/${course}`)
            const data = await response.json();
            setBooks(data);
            // window.location = "/";
        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(() => {
        getBooks();
    }, [])

    return (
        <div>
            <div class="container mt-5">
                <div>
                    <div class="form-group mx-sm-3 mb-2">
                        <input type="text" class="form-control mb-3" id="inputPassword2" placeholder="Enter your College! e.g. CSCE 121" onChange={e =>
                        setClass(e.target.value)}></input>
                        <input list="browsers">
                            <datalist id="browsers">
                                <option value="Internet Explorer" />
                                <option value="Firefox" />
                                <option value="Chrome" />
                                <option value="Opera" />
                                <option value="Safari" />
                            </datalist>
                        </input>
                        <input type="text" class="form-control" id="inputPassword2" placeholder="Enter your course! e.g. CSCE 121" onChange={e =>
                        setClass(e.target.value)}></input>
                    </div>
                    <button type="button" class="btn btn-primary mb-2" onClick={e => getSomeBooks(course)}>Search</button>
                    <button type="button" class="btn btn-primary mb-2 ml-4" onClick={e => getBooks()}>Show All</button>
                </div>
                <h4 class='mt-5 mb-5 result-number'>{books.length} results</h4>
                <BookRow books = {books}/>
            </div>
        </div>
    )
}
