import React, {useState, useEffect, useRef} from 'react'
import BookRow from './BookRow'
import BookSearch from './BookSearch'

export default function BooksPage() {

    const [books, setBooks] = useState([]);
    const [colleges, setColleges] = useState([]);
    const [course, setClass] = useState("");
    const [college, setCollege] = useState("");
    const testCollege = useRef()

    const getBooks = async () => {
        try {
            const response = await fetch('http://localhost:5000/getbooks')
            const data = await response.json();
            setBooks(data);
        } catch (error) {
            console.error(error.message);
        }
    }

    const getColleges = async () => {
        console.log("TEST")
        try {
            const data = await fetch('http://localhost:5000/colleges')
            const json_college = await data.json();
            setColleges(json_college);
        } catch (error) {
            console.error(error.message);
        }
    }

    const getSomeBooks = async (college, course) => {
        try {
            const response = await fetch(`http://localhost:5000/getbooks/${college}/${course}`)
            const data = await response.json();
            console.log(testCollege.current.value)
            setBooks(data);
            // window.location = "/";
        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(() => {
        getBooks();
        getColleges();
    }, [])

    const test = ['a', 'b'];

    return (
        <div>
            <div class="container mt-5">
                <div>
                    <div class="form-group mx-sm-3 mb-2">
                    <input class = 'form-control mb-3' type='text' list='data' placeholder='Select your college! e.g. Texas A&M University' onChange={e =>
                    setCollege(e.target.value)}/>
                        <datalist id="data">
                        {colleges.map(school => (                            
                            <option value = {school.college}/>
                        ))}
                        </datalist>

                    <input type="text" class="form-control" placeholder="Enter your course! e.g. CSCE 121" onChange={e =>
                    setClass(e.target.value)}></input>
                    <input ref = {testCollege} type='text' class='form-control' placeholder="TEST INPUT SPEED"></input>
                    </div>
                    <button type="button" class="btn btn-primary mb-2" onClick={e => getSomeBooks(college, course)}>Search</button>
                    <button type="button" class="btn btn-primary mb-2 ml-4" onClick={e => getBooks()}>Show All</button>
                </div>
                <h4 class='mt-5 mb-5 result-number'>{books.length} results</h4>
                <BookRow books = {books}/>
            </div>
        </div>
    )
}
