import React, {useState, useEffect, useRef} from 'react'

export default function Step1(props) {

    const [colleges, setColleges] = useState([]);

    const getColleges = async () => {
        console.log("TEST")
        try {
            const data = await fetch('http://localhost:5000/colleges')
            const json_college = await data.json();
            setColleges(json_college);
        } catch (error) {
            console.error(error.message);
        }
    }

    useEffect(() => {
        getColleges();
    }, [])

    return (
        <div>
            <h2>We need some basic details about you</h2>
            <h3>(This makes it easier for others to connect with you!)</h3>
            <p>
                Name:{" "}
                <input name="name" value={props.getState("name", "")} onChange={props.handleChange} />
            </p>
            <p>
                College:{" "}
                <input name="college" value={props.getState("college", "")} list='college' onChange={props.handleChange} placeholder='Texas A&M University'/>
                    <datalist id="college">
                        {colleges.map(school => (                            
                            <option value = {school.college}/>
                        ))}
                    </datalist>
            </p>
            <p>
                Email:{" "}
                <input type='email' name="email" value={props.getState("email", "")} onChange={props.handleChange} />
            </p>
            <p>
                <button disabled={true} onClick={props.prev}>
                    Previous
                </button>
                <button disabled={false} onClick={props.next}>
                    Next
                </button>
            </p>
        </div>
    )
}
